import { useEffect, useState } from "react";
import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableAction from "../../components/ui/tableActionTable";

export default function Tables() {
  const [searchTerm, setSearchTerm] = useState("");
  const [tablesData, setTablesData] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch tables + graph summary and merge
  const fetchTablesData = async () => {
    setLoading(true);
    try {
      // Fetch both endpoints in parallel
      const [tablesRes, graphRes] = await Promise.all([
        fetch("http://localhost:5500/tables"),
        fetch("http://localhost:5500/tables/graph/validate"),
      ]);

      const tables = await tablesRes.json();
      const graph = await graphRes.json();

      const summary = graph.summary || [];

      // Build lookup maps from summary
      const fkMap = {};
      const pkMap = {};
      summary.forEach((s) => {
        fkMap[s.table] = s.foreignKeyCount;
        pkMap[s.table] = s.primaryKeyColumns;
      });

      // Merge: tables info + summary info
      const merged = tables.map((t) => {
        const tableName = t.name; // /tables uses "name"
        return {
          ...t,
          name: tableName,
          totalRows: t.totalRows ?? "-",
          description: t.description ?? "-",
          primaryKeyColumns: pkMap[tableName] || [],
          foreignKeyCount: fkMap[tableName] ?? 0, // ✅ correct match
        };
      });

      setTablesData(merged);
    } catch (err) {
      console.error("Error fetching tables:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTablesData();
  }, []);

  const handleChange = (e) => setSearchTerm(e.target.value);
  const refreshCounts = () => fetchTablesData();

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={handleChange} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-end mb-2">
                    <button
                      onClick={refreshCounts}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>
                  {loading ? (
                    <div>Loading...</div>
                  ) : (
                    <TableAction
                      columns={[
                        "Name",
                        "total rows",
                        "total foreign key",
                        "description",
                        "action",
                      ]}
                      rows={tablesData.filter((t) =>
                        t.name.toLowerCase().includes(searchTerm.toLowerCase())
                      )}
                      title="Tables"
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
