import { useEffect, useState } from "react";
import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableActionUsers from "../../components/ui/tableActionUsers"; // can reuse same table component

export default function Tables() {
  const [tables, setTables] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5500/table/graph/validate", {
      headers: {
        "Authorization": `Bearer ${localStorage.getItem("token")}`,
      },
    })
      .then((res) => res.json())
      .then((data) => {
        const mapped = data.summary.map((t, idx) => ({
          id: idx + 1,
          name: t.table,
          primaryKeys: t.primaryKeyColumns.join(", ") || "—",
          foreignKeyCount: t.foreignKeyCount,
        }));
        setTables(mapped);
      })
      .catch(console.error);
  }, []);

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <h2 className="text-xl font-semibold mb-4">Tables</h2>
                  <TableActionUsers
                    columns={["ID", "Table Name", "Primary Keys", "Foreign Keys"]}
                    rows={tables}
                    title="Database Tables"
                  />
                </div>
              </div>
              {/* Keep the closing tag comment intact */}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
