import { useEffect, useState } from "react";

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5500/tables/graph/validate")
       .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch table data");
        return res.json();
      })
      .then((data) => {
        // Use the 'summary' array directly
        const tablesWithFK = data.summary.map((t, idx) => ({
          id: idx + 1, // assign sequential IDs
          name: t.table,
          primaryKeys: t.primaryKeyColumns,
          foreignKeyCount: t.foreignKeyCount,
        }));
        setTables(tablesWithFK);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const filteredTables = tables.filter((t) =>
    t.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <p>Loading tables...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Tables</h2>
      <input
        type="text"
        placeholder="Search tables..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: "1rem", padding: "0.5rem", width: "100%" }}
      />

      {filteredTables.length === 0 ? (
        <p>No tables found</p>
      ) : (
        <table border="1" cellPadding="8" cellSpacing="0" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Primary Keys</th>
              <th>Foreign Keys</th>
            </tr>
          </thead>
          <tbody>
            {filteredTables.map((table) => (
              <tr key={table.id}>
                <td>{table.id}</td>
                <td>{table.name}</td>
                <td>{table.primaryKeys.join(", ") || "—"}</td>
                <td>{table.foreignKeyCount}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
