import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableAction from "../../components/ui/tableActionTable";
import { useEffect, useState } from "react";

export default function Tables() {
  const [searchTerm, setSearchTerm] = useState("");
  const [allTables, setAllTables] = useState([]);

  const fetchTables = async () => {
    try {
      // Fetch table info
      const resTables = await fetch("http://localhost:5500/tables");
      const tablesData = await resTables.json();

      // Fetch correct FK counts
      const resFK = await fetch("http://localhost:5500/tables/graph/validate");
      const fkData = await resFK.json();

      // Merge FK counts into table info
      const mergedData = tablesData.map((table) => {
        const fkEntry = fkData.find((f) => f.name === table.name);
        return {
          ...table,
          totalForeignKey: fkEntry ? fkEntry.totalForeignKey : 0,
        };
      });

      setAllTables(mergedData);
    } catch (err) {
      console.error("Error fetching tables:", err);
    }
  };

  useEffect(() => {
    fetchTables();
  }, []);

  const handleChange = (event) => setSearchTerm(event.target.value);

  const refreshCounts = async () => {
    await fetch("http://localhost:5500/tables/refresh", { method: "PATCH" });
    fetchTables();
  };

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={handleChange} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-end mb-2">
                    <button
                      onClick={refreshCounts}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>
                  <TableAction
                    columns={["Name", "total rows", "total foreign key", "description", "action"]}
                    rows={allTables.filter((val) =>
                      searchTerm === "" ? true : val.name.toLowerCase().includes(searchTerm.toLowerCase())
                    )}
                    title="Tables"
                  />
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
