import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableAction from "../../components/ui/tableActionTable";
import { useEffect, useState } from "react";

export default function Tables() {
  const [searchTerm, setSearchTerm] = useState("");
  const [tablesData, setTablesData] = useState([]); // will include fk count, pk columns, etc.
  const [loading, setLoading] = useState(true);

  const fetchTablesData = () => {
    setLoading(true);
    // fetch both /tables and /tables/graph/validate
    Promise.all([
      fetch("http://localhost:5500/tables").then((r) => r.json()),
      fetch("http://localhost:5500/tables/graph/validate").then((r) => r.json()),
    ]).then(([tables, graph]) => {
      const summaryMap = {};
      (graph.summary || []).forEach((s) => {
        summaryMap[s.table] = s.foreignKeyCount;
      });

      // merge foreign key count into tables
      const merged = tables.map((t) => ({
        ...t,
        foreignKeyCount: summaryMap[t.name] || 0,
      }));

      setTablesData(merged);
    }).finally(() => setLoading(false));
  };

  useEffect(() => {
    fetchTablesData();
  }, []);

  const handleChange = (event) => setSearchTerm(event.target.value);

  const refreshCounts = () => fetchTablesData();

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={handleChange} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-end mb-2">
                    <button
                      onClick={refreshCounts}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>
                  {loading ? (
                    <div>Loading...</div>
                  ) : (
                    <TableAction
                      columns={[
                        "Name",
                        "total rows",
                        "total foreign key",
                        "description",
                        "action",
                      ]}
                      rows={tablesData.filter((val) =>
                        val.name.toLowerCase().includes(searchTerm.toLowerCase())
                      )}
                      title="Tables"
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
