// TableGraph.js
import { useEffect, useMemo, useRef, useState } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { useNavigate } from "react-router-dom";

export default function TableGraph({ graph, tableName, show, setShow, RelationsPk, RelationsFk }) {
  const fgRef = useRef();
  const navigate = useNavigate();
  const [tooltip, setTooltip] = useState({ visible: false, x: 0, y: 0, text: "" });

  // Track mouse position for tooltip placement
  useEffect(() => {
    const handleMove = (e) => {
      setTooltip((prev) => ({ ...prev, x: e.clientX, y: e.clientY }));
    };
    window.addEventListener("mousemove", handleMove);
    return () => window.removeEventListener("mousemove", handleMove);
  }, []);

  // Filter nodes and links, attach PK and FK info
  const filtered = useMemo(() => {
    if (!graph?.nodes || !graph?.links) return { nodes: [], links: [] };

    const mainNode = graph.nodes.find((n) => n.name === tableName);
    if (!mainNode) return { nodes: [], links: [] };

    // Build neighbors set
    const neighbors = new Set([mainNode.id]);
    const links = graph.links
      .filter((l) => {
        const srcId = typeof l.source === "object" ? l.source.id : l.source;
        const tgtId = typeof l.target === "object" ? l.target.id : l.target;
        if (srcId === mainNode.id || tgtId === mainNode.id) {
          neighbors.add(srcId);
          neighbors.add(tgtId);
          return true;
        }
        return false;
      })
      .map((l) => {
        const srcId = typeof l.source === "object" ? l.source.id : l.source;
        const tgtId = typeof l.target === "object" ? l.target.id : l.target;
        const srcNode = graph.nodes.find((n) => n.id === srcId);
        const tgtNode = graph.nodes.find((n) => n.id === tgtId);

        // Attach PK and FK columns
        const srcPK = RelationsPk?.filter(r => r.tableName === srcNode.name).map(r => r.name) || [];
        const srcFK = RelationsFk?.filter(r => r.tableName === srcNode.name).map(r => r.name) || [];
        const tgtPK = RelationsPk?.filter(r => r.tableName === tgtNode.name).map(r => r.name) || [];
        const tgtFK = RelationsFk?.filter(r => r.tableName === tgtNode.name).map(r => r.name) || [];

        return {
          ...l,
          source: { ...srcNode, pkColumns: srcPK, fkColumns: srcFK },
          target: { ...tgtNode, pkColumns: tgtPK, fkColumns: tgtFK },
          srcName: srcNode.name,
          tgtName: tgtNode.name,
        };
      });

    const nodes = graph.nodes
      .filter((n) => neighbors.has(n.id))
      .map((n) => {
        const pkCols = RelationsPk?.filter(r => r.tableName === n.name).map(r => r.name) || [];
        const fkCols = RelationsFk?.filter(r => r.tableName === n.name).map(r => r.name) || [];
        return { ...n, pkColumns: pkCols, fkColumns: fkCols };
      });

    return { nodes, links };
  }, [graph, tableName, RelationsPk, RelationsFk]);

  // Setup forces for spacing
  useEffect(() => {
    if (fgRef.current) {
      const fg = fgRef.current;
      fg.d3Force("charge").strength(-400);
      fg.d3Force("link").distance(200);
    }
  }, [filtered]);

  // Zoom controls
  const handleZoomIn = () => {
    if (fgRef.current) fgRef.current.zoom(fgRef.current.zoom() * 1.2, 400);
  };
  const handleZoomOut = () => {
    if (fgRef.current) fgRef.current.zoom(fgRef.current.zoom() / 1.2, 400);
  };
  const handleZoomToFit = () => {
    if (fgRef.current) fgRef.current.zoomToFit(400, 100);
  };

  return (
    <div className="w-full h-full bg-white rounded-lg shadow relative">
      <div className="flex justify-center items-center mx-auto mb-5 ">
        <h3 className="px-6 text-xl italic">Graph view</h3>
        <label htmlFor="ShowGraph" className="relative w-16 h-8 cursor-pointer">
          <input
            type="checkbox"
            id="ShowGraph"
            className="sr-only peer"
            onChange={() => setShow((prev) => !prev)}
            checked={show}
          />
          <span className="absolute inset-0 bg-orange-400 rounded-full transition peer-checked:bg-[#3e9688]" />
          <span className="absolute inset-0 w-6 h-6 m-1 bg-white rounded-full transition peer-checked:translate-x-8" />
        </label>
      </div>

      {show && (
        <div className="relative w-full h-full">
          {/* Zoom controls */}
          <div className="absolute top-3 right-3 flex flex-col gap-2 z-10">
            <button onClick={handleZoomIn} className="px-2 py-1 bg-gray-700 text-white rounded shadow">+</button>
            <button onClick={handleZoomOut} className="px-2 py-1 bg-gray-700 text-white rounded shadow">-</button>
            <button onClick={handleZoomToFit} className="px-2 py-1 bg-gray-700 text-white rounded shadow text-xs">Fit</button>
          </div>

          {/* Tooltip */}
          {tooltip.visible && (
            <div
              className="absolute bg-white text-gray-800 text-xs px-3 py-2 rounded-lg shadow-lg border border-[#3e9688] pointer-events-none z-50"
              style={{ left: tooltip.x + 12, top: tooltip.y + 12 }}
            >
              {tooltip.text}
            </div>
          )}

          <ForceGraph2D
            ref={fgRef}
            graphData={filtered}
            nodeId="id"
            nodeLabel={(n) =>
              `${n.name}\nPKs: ${n.pkColumns?.join(", ") || "(none)"}\nFKs: ${n.fkColumns?.join(", ") || "(none)"}`
            }
            linkDirectionalArrowLength={6}
            linkDirectionalArrowRelPos={1}
            linkCurvature={0.25}
            nodeAutoColorBy="group"
            onNodeClick={(node) => navigate(`/table/${node.name}`)}
            enableZoomPanInteraction={false}
            onLinkHover={(link) => {
              if (link) {
                setTooltip({
                  visible: true,
                  text: `${link.source.name} → ${link.target.name}\nPK: ${link.source.pkColumns?.join(", ") || "(none)"}\nFK: ${link.target.fkColumns?.join(", ") || "(none)"}`,
                  x: tooltip.x,
                  y: tooltip.y,
                });
              } else {
                setTooltip((prev) => ({ ...prev, visible: false }));
              }
            }}
            linkColor={(link) => {
              const tgtName = link.target.name || link.tgtName;
              return tgtName === tableName ? "#16a34a" : "#f59e0b"; 
            }}
            linkWidth={2}
            nodeCanvasObject={(node, ctx, globalScale) => {
              const label = node.name;
              const fontSize = 14 / Math.max(0.5, globalScale);

              ctx.beginPath();
              ctx.arc(node.x, node.y, node.name === tableName ? 12 : 8, 0, 2 * Math.PI, false);
              ctx.fillStyle = node.name === tableName ? "#f97316" : node.color || "#ccc";
              ctx.fill();
              ctx.strokeStyle = "#333";
              ctx.lineWidth = 2;
              ctx.stroke();

              ctx.font = `${fontSize}px Sans-Serif`;
              ctx.textAlign = "center";
              ctx.textBaseline = "top";
              ctx.fillStyle = "#111";
              ctx.fillText(label, node.x, node.y + 14);
            }}
          />
        </div>
      )}
    </div>
  );
}
