import { useEffect, useState, useMemo } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import background from "../../../assets/images/background4.png";
import logo from "../../../assets/images/logo.png";

import Alert from "../../../components/ui/Alert";
import RoundedIconButton from "../../../components/ui/roundedIconButton";
import { TOAST } from "../../../data/toast";

import Columns from "./columns";
import ColumnsFk from "./columnsFk";
import InputRow from "./inputRow";
import RelationsFk from "./relationsFk";
import RelationsPk from "./relationsPk";
import Result from "./result";
import TableGraph from "./TableGraph";

export default function TableDetail() {
  const navigate = useNavigate();
  const { name } = useParams();

  const [table, setTable] = useState({});
  const [attributes, setAttributes] = useState([]);
  const [relationsP, setRelationsP] = useState([]);
  const [inputs, setInputs] = useState({});
  const [rowsDB, setRowsDB] = useState({});
  const [shown, setShown] = useState([]);
  const [showGraph, setShowGraph] = useState(true);
  const [showDetail, setShowDetail] = useState(true);
  const [localGraph, setLocalGraph] = useState({ nodes: [], links: [] });

  // Fetch table info and attributes
  useEffect(() => {
    fetch(`http://localhost:5500/attributes/table/${data.id}`)
  .then((res) => res.json())
  .then((attrData) => {
    setAttributes(attrData);

    // Map nodes after attributes are fetched
    fetch("http://localhost:5500/tables/graph/view")
      .then((res) => res.json())
      .then((graph) => {
        const node = graph.nodes.find((n) => n.name === name);
        if (!node) return;

        const neighbors = new Set([node.id]);
        graph.links.forEach((l) => {
          if (l.source === node.id) neighbors.add(l.target);
          if (l.target === node.id) neighbors.add(l.source);
        });

        const nodes = graph.nodes.filter((n) => neighbors.has(n.id)).map((n) => {
          const attrs = attrData.filter((a) => a.tableId === n.id); // now attrData is defined
          return {
            ...n,
            pkColumns: attrs.filter((a) => a.isPrimaryKey).map((a) => a.name),
            fkColumns: attrs.filter((a) => a.fTable).map((a) => a.name),
          };
        });

        const links = graph.links.filter(
          (l) => neighbors.has(l.source) && neighbors.has(l.target)
        );

        setLocalGraph({ nodes, links });
      });
      })
      .catch(() => navigate("/404"));
  }, [name]);

  const handleSubmit = (event) => {
    event.preventDefault();
    setRowsDB({});
    Object.keys(inputs).forEach((key) => {
      if (inputs[key] && !isNaN(inputs[key])) {
        fetch(`http://localhost:5500/tables/search/${key}/PK/${inputs[key]}`)
          .then((res) => res.json())
          .then((data) => {
            if (data.length > 0) {
              setRowsDB((prev) => ({ ...prev, [key]: data }));
            } else {
              setShown((prev) => [
                ...prev,
                <Alert
                  key={key}
                  title={TOAST.warning.title}
                  text="There is no row with this primary key."
                  color={TOAST.warning.color}
                  svg={TOAST.warning.svg}
                />,
              ]);
            }
          })
          .catch(() => {
            setRowsDB({});
            setInputs({});
          });
      } else {
        setShown((prev) => [
          ...prev,
          <Alert
            key={key}
            title={TOAST.error.title}
            text="Please enter a valid value (number)."
            color={TOAST.error.color}
            svg={TOAST.error.svg}
          />,
        ]);
      }
    });
  };

  // Components
  const columns = Columns(attributes);
  const columnsFk = ColumnsFk(attributes);
  const relationsPk = RelationsPk(relationsP, attributes, table);
  const relationsFk = RelationsFk(attributes, table);
  const inputRow = InputRow(attributes, setInputs);
  const result = Result(rowsDB);

  return (
    <div className="h-screen w-screen">
      {/* Notifications */}
      <div className="fixed bottom-3 right-0 flex flex-col items-end gap-1 z-30">
        {shown}
      </div>

      {/* Header */}
      <div
        className="px-8 py-2 text-left mb-5 h-36 bg-gradient-to-r bg-cover bg-left-top from-teal-400 to-orange-300 w-full"
        style={{ backgroundImage: `url(${background})` }}
      >
        <span className="absolute top-0 left-0 m-0 p-0 bg-gray-700 opacity-20 w-full h-36"></span>
        <Link to="/home" className="flex flex-col mt-4 items-center justify-center">
          <img src={logo} alt="logo" className="h-12 z-20" />
          <h2 className="text-2xl font-medium text-white cursor-default tracking-wider z-20">
            TestBench-VS
          </h2>
        </Link>
      </div>

      {/* Table Info */}
      <div className="flex flex-col justify-center items-center">
        <div className="max-w-2xl mx-auto sm:max-w-xl md:max-w-2xl mt-6 text-center">
          <p className="inline-block px-3 py-2 mb-4 text-xs font-semibold tracking-wider text-white uppercase rounded-full bg-orange-500">
            {table.rowCount} Row
          </p>
          <h2 className="max-w-lg mb-6 font-sans text-3xl font-bold leading-none tracking-tight text-gray-900 sm:text-4xl md:mx-auto flex justify-start">
            <span className="relative inline-block">
              <span className="z-20 text-left bg-white"> {table.name}</span>
            </span>
          </h2>
          <p className="text-base text-gray-700 md:text-lg">
            {table.description || "No description yet"}
          </p>
        </div>
      </div>

      {/* Graph */}
      {showGraph && (
        <div className="flex-1 w-full flex justify-center items-center p-4">
          <TableGraph
            graph={localGraph}
            tableName={name}
            attributes={attributes}
            relations={relationsP}
          />
        </div>
      )}

      {/* Meta Data & Data Search */}
      {showDetail ? (
        <div className="relative flex flex-col lg:flex-row justify-start items-start w-full px-4 lg:px-20 pb-4 gap-2 mt-5 m-5">
          <div className="w-full flex flex-col gap-2 basis-7/12">
            <div className="flex flex-col gap-2 w-full p-5">
              <h2 className="mb-4 text-lg w-max border-b-2 border-orange-500">Columns</h2>
              <div className="flex flex-col gap-2 w-full border-b border-dashed border-[#3e9688] pb-1">
                {columns}
              </div>
            </div>

            <div className="w-full p-5 bg-white rounded-lg text-neutral-900">
              <h2 className="mb-4 w-max text-lg border-b-2 border-[#3e9688] border-dashed">
                Foreign Keys
              </h2>
              <div className="flex flex-col gap-2 w-full">
                {columnsFk.length > 0 ? columnsFk : "This table doesn't have any foreign key."}
              </div>
            </div>
          </div>

          <div className="w-full flex flex-col gap-2 mt-9 lg:mt-0 p-5 border-2 border-orange-600 border-dashed rounded-lg">
            <h2 className="mb-4 text-lg border-b-2 border-[#3e9688]">Relations</h2>
            <div className="flex flex-col gap-2">
              {relationsPk.length > 0 || relationsFk.length > 0
                ? [relationsFk, relationsPk]
                : "This table is not linked to any other table."}
            </div>
          </div>
        </div>
      ) : (
        <div className="flex flex-col justify-start items-center gap-6 m-3 p-2 w-full">
          <form
            className="flex justify-center items-center flex-col flex-wrap lg:flex-row h-min gap-3 w-fit"
            onSubmit={handleSubmit}
          >
            <div className="flex justify-center items-center flex-col md:flex-row gap-2">
              {inputRow}
            </div>
            <div className="lg:self-end -mb-1">
              {inputRow.length > 0 && (
                <RoundedIconButton text="search" handleClick={handleSubmit} />
              )}
            </div>
          </form>
          <div className="flex justify-center items-start flex-wrap gap-3">{result}</div>
        </div>
      )}
    </div>
  );
}
