import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableAction from "../../components/ui/tableActionTable";
import { useEffect, useState } from "react";

export default function Tables() {
  const [searchTerm, setSearchTerm] = useState("");
  const [allTables, setAllTables] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch tables with foreign key counts
  const fetchTables = () => {
    setLoading(true);
    fetch("http://localhost:5500/tables/graph/validate")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch table data");
        return res.json();
      })
      .then((data) => {
        const tablesWithFK = data.summary.map((t, idx) => ({
          id: idx + 1,
          name: t.table,
          totalRows: t.totalRows || 0, // optional if your API provides total rows
          foreignKeyCount: t.foreignKeyCount || 0,
          description: t.description || "",
        }));
        setAllTables(tablesWithFK);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchTables();
  }, []);

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const refreshCounts = () => {
    fetch("http://localhost:5500/tables/refresh", { method: "PATCH" })
      .then((r) => r.json())
      .then(() => fetchTables());
  };

  const filteredTables = allTables.filter((val) =>
    val.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading)
    return (
      <div className="flex justify-center items-center h-screen">
        <p>Loading tables...</p>
      </div>
    );

  if (error)
    return (
      <div className="flex justify-center items-center h-screen">
        <p>Error: {error}</p>
      </div>
    );

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={handleChange} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-end mb-2">
                    <button
                      onClick={refreshCounts}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>
                  <TableAction
                    columns={[
                      "Name",
                      "Total Rows",
                      "Total Foreign Keys",
                      "Description",
                      "Action",
                    ]}
                    rows={filteredTables}
                    title="Tables"
                  />
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
