import { useEffect, useState } from "react";

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("/table/graph/validate")
      .then((res) => {
        if (!res.ok) throw new Error("Failed to fetch table graph");
        return res.json();
      })
      .then((graph) => {
        // graph expected: { nodes: [...], links: [...] }
        const fkCounts = {};

        // Count how many foreign keys point to each table
        graph.links.forEach((link) => {
          const targetTable = link.targetTableName || link.target?.name;
          if (!fkCounts[targetTable]) fkCounts[targetTable] = 0;
          fkCounts[targetTable]++;
        });

        // Map nodes to tables and attach foreign key counts
        const tablesWithFk = graph.nodes.map((node) => ({
          id: node.id,
          name: node.name,
          rowCount: node.rowCount ?? 0,
          numForeignKey: fkCounts[node.name] ?? 0,
        }));

        setTables(tablesWithFk);
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError(err.message);
        setLoading(false);
      });
  }, []);

  const filteredTables = tables.filter((t) =>
    t.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <p>Loading tables...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Tables</h2>
      <input
        type="text"
        placeholder="Search tables..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: "1rem", padding: "0.5rem", width: "100%" }}
      />

      {filteredTables.length === 0 ? (
        <p>No tables found</p>
      ) : (
        <table border="1" cellPadding="8" cellSpacing="0" style={{ width: "100%" }}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Rows</th>
              <th>Foreign Keys</th>
            </tr>
          </thead>
          <tbody>
            {filteredTables.map((table) => (
              <tr key={table.id}>
                <td>{table.id}</td>
                <td>{table.name}</td>
                <td>{table.rowCount}</td>
                <td>{table.numForeignKey}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
