import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import background from "../../../assets/images/background4.png";
import logo from "../../../assets/images/logo.png";
import Alert from "../../../components/ui/Alert";
import RoundedIconButton from "../../../components/ui/roundedIconButton";
import { TOAST } from "../../../data/toast";
import Columns from "./columns";
import ColumnsFk from "./columnsFk";
import InputRow from "./inputRow";
import RelationsFk from "./relationsFk";
import RelationsPk from "./relationsPk";
import Result from "./result";
import TableGraph from "./TableGraph";

export default function TableDetail() {
  let navigate = useNavigate();
  const { name } = useParams();
  const [table, setTable] = useState({});
  const [shown, setShown] = useState([]);
  const [attributes, setAttributes] = useState([]);
  const [inputs, setInputs] = useState({});
  const [rowsDB, setRowsDB] = useState({});
  const [showDetail, setShowDetail] = useState(true);
  const [showGraph, setShowGraph] = useState(true);
  const [relationsP, setRelationsP] = useState([]);
  const [localGraph, setLocalGraph] = useState({ nodes: [], links: [] });

  useEffect(() => {
    fetch("http://localhost:5500/tables/one/" + name)
      .then((res) => res.json())
      .then((data) => {
        setTable(data);
        fetch("http://localhost:5500/attributes/table/" + data.id)
          .then((res) => res.json())
          .then((data) => setAttributes(data));
        fetch("http://localhost:5500/attributes/name/" + data.name)
          .then((res) => res.json())
          .then((data) => {
            setRelationsP(data);
          });
        fetch("http://localhost:5500/tables/graph/view")
          .then((res) => res.json())
          .then((g) => {
            const node = g.nodes.find((n) => n.name === name);
            if (!node) return;
            const neighbors = new Set([node.id]);
            g.links.forEach((l) => {
              if (l.source === node.id) neighbors.add(l.target);
              if (l.target === node.id) neighbors.add(l.source);
            });
            const nodes = g.nodes.filter((n) => neighbors.has(n.id));
            const links = g.links.filter(
              (l) => neighbors.has(l.source) && neighbors.has(l.target)
            );
            setLocalGraph({ nodes, links });
          });
      })
      .catch(() => {
        navigate("/404");
      });
  }, [name]); // eslint-disable-line react-hooks/exhaustive-deps

  const handleSubmit = (event) => {
    setRowsDB({});
    Object.keys(inputs).forEach(async function (key, index) {
      if (inputs[key] && !isNaN(inputs[key])) {
        fetch(`http://localhost:5500/tables/search/${key}/PK/${inputs[key]}`)
          .then((res) => res.json())
          .then((data) => {
            if (data.length > 0) {
              setRowsDB((prev) => {
                return {
                  ...prev,
                  [key]: data,
                };
              });
            } else {
              setShown((prev) => [
                ...prev,
                <Alert
                  title={TOAST.warning.title}
                  text="There is not row with this primary key."
                  color={TOAST.warning.color}
                  svg={TOAST.warning.svg}
                  key={5}
                />,
              ]);
            }
          })
          .catch(() => {
            setRowsDB({});
            setInputs({});
          });
      } else {
        setShown((prev) => [
          ...prev,
          <Alert
