import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableAction from "../../components/ui/tableActionTable";
import { useEffect, useState } from "react";

export default function Tables() {
  const [searchTerm, setSearchTerm] = useState("");
  const [tablesData, setTablesData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchTablesData = async () => {
    setLoading(true);
    try {
      // fetch summary like dashboard
      const graphRes = await fetch("http://localhost:5500/tables/graph/validate");
      const graphData = await graphRes.json();

      // use only summary
      const summary = graphData.summary || [];

      // map summary into rows for TableAction
      const rows = summary.map((s) => ({
        name: s.table,
        totalRows: s.totalRows || "-", // if your backend provides this
        foreignKeyCount: s.foreignKeyCount,
        description: s.description || "-",
      }));

      setTablesData(rows);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTablesData();
  }, []);

  const handleChange = (e) => setSearchTerm(e.target.value);
  const refreshCounts = () => fetchTablesData();

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={handleChange} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-end mb-2">
                    <button
                      onClick={refreshCounts}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>
                  {loading ? (
                    <div>Loading...</div>
                  ) : (
                    <TableAction
                      columns={["Name", "total rows", "total foreign key", "description", "action"]}
                      rows={tablesData.filter((t) =>
                        t.name.toLowerCase().includes(searchTerm.toLowerCase())
                      )}
                      title="Tables"
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
