// TableGraph.js
import { useEffect, useMemo, useRef, useState } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { useNavigate } from "react-router-dom";
import * as d3 from "d3-force";

export default function TableGraph({ graph, tableName, show, setShow, RelationsPk, RelationsFk }) {
  const fgRef = useRef();
  const navigate = useNavigate();
  const [tooltip, setTooltip] = useState({ visible: false, x: 0, y: 0, text: "" });

  // Mouse tracking for tooltip
  useEffect(() => {
    const handleMove = (e) => setTooltip(prev => ({ ...prev, x: e.clientX, y: e.clientY }));
    window.addEventListener("mousemove", handleMove);
    return () => window.removeEventListener("mousemove", handleMove);
  }, []);

  // Filter nodes and links
  const filtered = useMemo(() => {
    if (!graph?.nodes || !graph?.links) return { nodes: [], links: [] };
    const main = graph.nodes.find(n => n.name === tableName);
    if (!main) return { nodes: [], links: [] };

    const neighbors = new Set([main.id]);
    const nodesById = Object.fromEntries(graph.nodes.map(n => [n.id, n]));

    const links = graph.links
      .map(l => {
        const srcId = typeof l.source === "object" ? l.source.id : l.source;
        const tgtId = typeof l.target === "object" ? l.target.id : l.target;
        if (!nodesById[srcId] || !nodesById[tgtId]) return null;

        if (srcId === main.id || tgtId === main.id) {
          neighbors.add(srcId);
          neighbors.add(tgtId);
          return {
            source: srcId, // **use id, not object**
            target: tgtId, // **use id, not object**
            srcName: nodesById[srcId].name,
            tgtName: nodesById[tgtId].name,
            pkColumns: nodesById[tgtId].pkColumns || [],
            fkColumns: nodesById[srcId].fkColumns || [],
            relation:
              RelationsPk?.[`${nodesById[tgtId].name}->${nodesById[srcId].name}`] ||
              RelationsPk?.[`${nodesById[srcId].name}->${nodesById[tgtId].name}`] ||
              RelationsFk?.[`${nodesById[tgtId].name}->${nodesById[srcId].name}`] ||
              RelationsFk?.[`${nodesById[srcId].name}->${nodesById[tgtId].name}`] ||
              null,
          };
        }
        return null;
      })
      .filter(Boolean);

    const nodes = graph.nodes
      .filter(n => neighbors.has(n.id))
      .map(n => ({
        ...n,
        fkColumns: links
          .filter(l => l.source === n.id)
          .map(l => l.fkColumns)
          .flat(),
      }));

    return { nodes, links };
  }, [graph, tableName, RelationsPk, RelationsFk]);

  // Setup forces
  useEffect(() => {
    if (fgRef.current) {
      const fg = fgRef.current;
      fg.d3Force("charge").strength(-100); // less repulsion
      fg.d3Force("link").distance(120).strength(1); // strong link force
      fg.d3Force("center", d3.forceCenter());
      fg.d3Force("collision", d3.forceCollide(30));
    }
  }, [filtered]);

  // Zoom controls
  const handleZoomIn = () => fgRef.current && fgRef.current.zoom(fgRef.current.zoom() * 1.2, 400);
  const handleZoomOut = () => fgRef.current && fgRef.current.zoom(fgRef.current.zoom() / 1.2, 400);
  const handleZoomToFit = () => fgRef.current && fgRef.current.zoomToFit(400, 100);

  return (
    <div className="w-full h-full bg-white rounded-lg shadow relative">
      <div className="flex justify-center items-center mx-auto mb-5">
        <h3 className="px-6 text-xl italic">Graph view</h3>
        <label htmlFor="ShowGraph" className="relative w-16 h-8 cursor-pointer">
          <input
            type="checkbox"
            id="ShowGraph"
            className="sr-only peer"
            onChange={() => setShow(prev => !prev)}
            checked={show}
          />
          <span className="absolute inset-0 bg-orange-400 rounded-full transition peer-checked:bg-[#3e9688]" />
          <span className="absolute inset-0 w-6 h-6 m-1 bg-white rounded-full transition peer-checked:translate-x-8" />
        </label>
      </div>

      {show && (
        <div className="relative w-full h-full">
          <div className="absolute top-3 right-3 flex flex-col gap-2 z-10">
            <button onClick={handleZoomIn} className="px-2 py-1 bg-gray-700 text-white rounded shadow">+</button>
            <button onClick={handleZoomOut} className="px-2 py-1 bg-gray-700 text-white rounded shadow">-</button>
            <button onClick={handleZoomToFit} className="px-2 py-1 bg-gray-700 text-white rounded shadow text-xs">Fit</button>
          </div>

          {tooltip.visible && (
            <div
              className="absolute bg-white text-gray-800 text-xs px-3 py-2 rounded-lg shadow-lg border border-[#3e9688] pointer-events-none z-50"
              style={{ left: tooltip.x + 12, top: tooltip.y + 12 }}
            >
              {tooltip.text}
            </div>
          )}

          <ForceGraph2D
            ref={fgRef}
            graphData={filtered}
            nodeId="id"
            nodeLabel={n => `${n.name}\nPKs: ${n.pkColumns?.join(",") || "(none)"}`}
            linkDirectionalArrowLength={6}
            linkDirectionalArrowRelPos={1}
            linkCurvature={0.25}
            nodeAutoColorBy="group"
            enableZoomPanInteraction={true}
            onNodeClick={node => navigate(`/table/${node.name}`)}
            onLinkHover={link => {
              if (link) {
                setTooltip({
                  visible: true,
                  text: `${link.tgtName} (${link.pkColumns?.join(",") || "none"}) -> ${link.srcName} FK (${link.fkColumns?.join(",") || "none"})`,
                  x: tooltip.x,
                  y: tooltip.y,
                });
              } else setTooltip(prev => ({ ...prev, visible: false }));
            }}
            linkColor={link => (link.target === tableName ? "#16a34a" : "#f59e0b")}
            linkWidth={2}
            nodeCanvasObject={(node, ctx, globalScale) => {
              const label = node.name;
              const fontSize = 14 / Math.max(0.5, globalScale);

              ctx.beginPath();
              ctx.arc(node.x, node.y, node.name === tableName ? 12 : 8, 0, 2 * Math.PI);
              ctx.fillStyle = node.name === tableName ? "#f97316" : node.color || "#ccc";
              ctx.fill();
              ctx.strokeStyle = "#333";
              ctx.lineWidth = 2;
              ctx.stroke();

              ctx.font = `${fontSize}px Sans-Serif`;
              ctx.textAlign = "center";
              ctx.textBaseline = "top";
              ctx.fillStyle = "#111";
              ctx.fillText(label, node.x, node.y + 14);
            }}
          />
        </div>
      )}
    </div>
  );
}
