import { useEffect, useState } from "react";
import NavbarAdmin from "../../components/navBarAdmin";
import SideBar from "../../components/sideBar";
import TableActionUsers from "../../components/ui/tableActionUsers"; // reuse your table component

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTables = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:5500/tables/graph/validate", {
        headers: {
          "Authorization": `Bearer ${localStorage.getItem("token")}`,
        },
      });
      if (!res.ok) throw new Error("Failed to fetch table data");
      const data = await res.json();

      const mapped = data.summary.map((t, idx) => ({
        id: idx + 1,
        name: t.table,
        primaryKeys: t.primaryKeyColumns,
        foreignKeyCount: t.foreignKeyCount,
      }));
      setTables(mapped);
    } catch (err) {
      console.error(err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTables();
  }, []);

  const filteredTables = tables.filter((t) =>
    t.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="flex overflow-hidden h-screen w-screen">
      <SideBar />
      <div className="flex flex-col flex-1 w-0 overflow-hidden">
        <NavbarAdmin setVal={(e) => setSearchTerm(e)} />
        <main className="relative flex-1 overflow-y-auto focus:outline-none bg-slate-50">
          <div className="py-6">
            <div className="px-4 mx-auto sm:px-6 md:px-8">
              <div className="py-4">
                <div className="rounded-lg bg-white p-3">
                  <div className="flex justify-between items-center mb-4">
                    <h2 className="text-xl font-semibold">Tables</h2>
                    <button
                      onClick={fetchTables}
                      className="px-3 py-1 text-sm rounded bg-teal-600 text-white hover:bg-teal-700"
                    >
                      Refresh counts
                    </button>
                  </div>

                  {loading ? (
                    <p>Loading tables...</p>
                  ) : error ? (
                    <p className="text-red-600">Error: {error}</p>
                  ) : filteredTables.length === 0 ? (
                    <p>No tables found</p>
                  ) : (
                    <TableActionUsers
                      columns={["#", "Name", "Primary Keys", "Foreign Keys"]}
                      rows={filteredTables}
                      title="Database Tables"
                    />
                  )}
                </div>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
}
