// TableGraph.js
import { useMemo, useRef } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { useNavigate } from "react-router-dom";

export default function TableGraph({ graph, tableName, show, setShow }) {
  const fgRef = useRef();
  const navigate = useNavigate();

  // Filter: only main table + directly connected nodes, links only to/from main table
  const filtered = useMemo(() => {
    if (!graph?.nodes || !graph?.links) return { nodes: [], links: [] };

    const main = graph.nodes.find((n) => n.name === tableName);
    if (!main) return { nodes: [], links: [] };

    const neighbors = new Set([main.id]);
    const links = graph.links.filter((l) => {
      const src = typeof l.source === "object" ? l.source.id : l.source;
      const tgt = typeof l.target === "object" ? l.target.id : l.target;
      if (src === main.id || tgt === main.id) {
        neighbors.add(src);
        neighbors.add(tgt);
        return true;
      }
      return false;
    });

    const nodes = graph.nodes.filter((n) => neighbors.has(n.id));
    return { nodes, links };
  }, [graph, tableName]);

  // Zoom controls
  const handleZoomIn = () => {
    if (fgRef.current) {
      const zoom = fgRef.current.zoom();
      fgRef.current.zoom(zoom * 1.2, 400);
    }
  };
  const handleZoomOut = () => {
    if (fgRef.current) {
      const zoom = fgRef.current.zoom();
      fgRef.current.zoom(zoom / 1.2, 400);
    }
  };
  const handleZoomToFit = () => {
    if (fgRef.current) {
      fgRef.current.zoomToFit(400, 100);
    }
  };

  return (
    <div className="w-full h-[600px] bg-white rounded-lg shadow relative">
      {/* Toggle button */}
      <div className="flex justify-between items-center p-2 border-b">
        <div className="font-semibold text-sm">Relationships graph</div>
        <button
          className="px-3 py-1 bg-orange-500 text-white text-xs rounded"
          onClick={() => setShow((prev) => !prev)}
        >
          {show ? "Hide Graph" : "Show Graph"}
        </button>
      </div>

      {show && (
        <div className="relative w-full h-full">
          {/* Zoom controls */}
          <div className="absolute top-3 right-3 flex flex-col gap-2 z-10">
            <button
              onClick={handleZoomIn}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow"
            >
              +
            </button>
            <button
              onClick={handleZoomOut}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow"
            >
              -
            </button>
            <button
              onClick={handleZoomToFit}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow text-xs"
            >
              Fit
            </button>
          </div>

          {/* Graph */}
          <ForceGraph2D
            ref={fgRef}
            graphData={filtered}
            nodeId="id"
            nodeLabel={(n) => `${n.name}\nPKs: ${n.pkColumns?.join(", ") || "(none)"}`}
            linkDirectionalArrowLength={6}
            linkDirectionalArrowRelPos={1}
            linkCurvature={0.25}
            nodeAutoColorBy="group"
            onNodeClick={(node) => navigate(`/table/${node.name}`)}
            nodeCanvasObject={(node, ctx, globalScale) => {
              const label = node.name;
              const fontSize = 14 / Math.max(0.5, globalScale);

              // Circle (highlight main table)
              ctx.beginPath();
              ctx.arc(node.x, node.y, node.name === tableName ? 12 : 8, 0, 2 * Math.PI, false);
              ctx.fillStyle = node.name === tableName ? "#f97316" : node.color || "#ccc";
              ctx.fill();
              ctx.strokeStyle = "#333";
              ctx.lineWidth = 2;
              ctx.stroke();

              // Label offset above node
              ctx.font = `${fontSize}px Sans-Serif`;
              ctx.textAlign = "center";
              ctx.textBaseline = "top";
              ctx.fillStyle = "#111";
              ctx.fillText(label, node.x, node.y + 12);
            }}
          />
        </div>
      )}
    </div>
  );
}
