// TableGraph.js
import { useEffect, useMemo, useRef, useState } from "react";
import ForceGraph2D from "react-force-graph-2d";
import { useNavigate } from "react-router-dom";
import RelationsPk from "./relationsPk";
export default function TableGraph({ graph, tableName, show, setShow, attributes, relations, table }) {

   useEffect(() => {
    fetch("http://localhost:5500/tables/one/" + name)
      .then((res) => res.json())
      .then((data) => {
        setTable(data);

        // fetch attributes
        fetch("http://localhost:5500/attributes/table/" + data.id)
          .then((res) => res.json())
          .then((data) => setAttributes(data));

        // fetch relations (pk/fk)
        fetch("http://localhost:5500/attributes/name/" + data.name)
          .then((res) => res.json())
          .then((data) => setRelationsP(data));

        // fetch graph
        fetch("http://localhost:5500/tables/graph/view")
          .then((res) => res.json())
          .then((g) => {
            const node = g.nodes.find((n) => n.name === name);
            if (!node) return;
            const neighbors = new Set([node.id]);
            g.links.forEach((l) => {
              if (l.source === node.id) neighbors.add(l.target);
              if (l.target === node.id) neighbors.add(l.source);
            });
            const nodes = g.nodes.filter((n) => neighbors.has(n.id));
            const links = g.links.filter(
              (l) => neighbors.has(l.source) && neighbors.has(l.target)
            );
            setLocalGraph({ nodes, links });
          });
      })
      .catch(() => {
        console.error("Could not load table data");
      });
  }, [name]);

  const fgRef = useRef();
  const navigate = useNavigate();
  const [tooltip, setTooltip] = useState({ visible: false, x: 0, y: 0, text: "" });

  // Track mouse position for tooltip placement
  useEffect(() => {
    const handleMove = (e) => {
      setTooltip((prev) => ({ ...prev, x: e.clientX, y: e.clientY }));
    };
    window.addEventListener("mousemove", handleMove);
    return () => window.removeEventListener("mousemove", handleMove);
  }, []);

  // Filter: only main table + directly connected nodes, links only to/from main table
  const filtered = useMemo(() => {
    if (!graph?.nodes || !graph?.links) return { nodes: [], links: [] };

    const main = graph.nodes.find((n) => n.name === tableName);
    if (!main) return { nodes: [], links: [] };

    const neighbors = new Set([main.id]);
    const links = graph.links.filter((l) => {
      const src = typeof l.source === "object" ? l.source.id : l.source;
      const tgt = typeof l.target === "object" ? l.target.id : l.target;
      if (src === main.id || tgt === main.id) {
        neighbors.add(src);
        neighbors.add(tgt);
        return true;
      }
      return false;
    });

    const nodes = graph.nodes.filter((n) => neighbors.has(n.id));
    return { nodes, links };
  }, [graph, tableName]);

  // Setup forces for spacing
  useEffect(() => {
    if (fgRef.current) {
      const fg = fgRef.current;
      fg.d3Force("charge").strength(-400); // push nodes apart
      fg.d3Force("link").distance(200); // spread links further
    }
  }, [filtered]);

  // Zoom controls
  const handleZoomIn = () => {
    if (fgRef.current) {
      const zoom = fgRef.current.zoom();
      fgRef.current.zoom(zoom * 1.2, 400);
    }
  };
  const handleZoomOut = () => {
    if (fgRef.current) {
      const zoom = fgRef.current.zoom();
      fgRef.current.zoom(zoom / 1.2, 400);
    }
  };
  const handleZoomToFit = () => {
    if (fgRef.current) {
      fgRef.current.zoomToFit(400, 100);
    }
  };

  return (
    <div className="w-full h-full bg-white rounded-lg shadow relative">
      <div className="flex justify-center items-center mx-auto mb-5 ">
        <h3 className="px-6 text-xl italic">Graph view</h3>
        <label htmlFor="ShowGraph" className="relative w-16 h-8 cursor-pointer">
          <input
            type="checkbox"
            id="ShowGraph"
            className="sr-only peer"
            onChange={() => setShow((prev) => !prev)}
            checked={show}
          />
          <span className="absolute inset-0 bg-orange-400 rounded-full transition peer-checked:bg-[#3e9688]" />
          <span className="absolute inset-0 w-6 h-6 m-1 bg-white rounded-full transition peer-checked:translate-x-8" />
        </label>
      </div>

      {show && (
        <div className="relative w-full h-full">
          {/* Zoom controls */}
          <div className="absolute top-3 right-3 flex flex-col gap-2 z-10">
            <button
              onClick={handleZoomIn}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow"
            >
              +
            </button>
            <button
              onClick={handleZoomOut}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow"
            >
              -
            </button>
            <button
              onClick={handleZoomToFit}
              className="px-2 py-1 bg-gray-700 text-white rounded shadow text-xs"
            >
              Fit
            </button>
          </div>

          {/* Floating tooltip */}
          {tooltip.visible && (
            <div
              className="absolute bg-white text-gray-800 text-xs px-3 py-2 rounded-lg shadow-lg border border-[#3e9688] pointer-events-none z-50"
              style={{ left: tooltip.x + 12, top: tooltip.y + 12 }}
            >
              {tooltip.text}
            </div>
          )}

          <ForceGraph2D
            ref={fgRef}
            graphData={filtered}
            nodeId="id"
            nodeLabel={(n) =>
              `${n.name}\nPKs: ${n.pkColumns?.join(", ") || "(none)"}`
            }
            linkDirectionalArrowLength={6}
            linkDirectionalArrowRelPos={1}
            linkCurvature={0.25}
            nodeAutoColorBy="group"
            onNodeClick={(node) => navigate(`/table/${node.name}`)}
            enableZoomPanInteraction={false} // disable scroll zoom

            onLinkHover={(link) => {
              if (link) {
                const relation = relations.find(
                  (r) => r.fk_table === link.target && r.pk_table === link.source
                );

                if (relation) {
                  const pkAttr = attributes.find((a) => a.id === relation.pk_attr)?.name || "N/A";
                  const fkAttr = attributes.find((a) => a.id === relation.fk_attr)?.name || "N/A";

                  setTooltip({
                    x: link.source.x,
                    y: link.source.y,
                    text: `PK: ${pkAttr} → FK: ${fkAttr}`,
                  });
                }
              } else {
                setTooltip(null);
              }
            }}

            linkColor={(link) => {
              const src =
                typeof link.source === "object" ? link.source.id : link.source;
              const tgt =
                typeof link.target === "object" ? link.target.id : link.target;
              return tgt === tableName ? "#16a34a" : "#f59e0b"; // green incoming, orange outgoing
            }}
            linkWidth={2}

            nodeCanvasObject={(node, ctx, globalScale) => {
              const label = node.name;
              const fontSize = 14 / Math.max(0.5, globalScale);

              ctx.beginPath();
              ctx.arc(
                node.x,
                node.y,
                node.name === tableName ? 12 : 8,
                0,
                2 * Math.PI,
                false
              );
              ctx.fillStyle =
                node.name === tableName ? "#f97316" : node.color || "#ccc";
              ctx.fill();
              ctx.strokeStyle = "#333";
              ctx.lineWidth = 2;
              ctx.stroke();

              ctx.font = `${fontSize}px Sans-Serif`;
              ctx.textAlign = "center";
              ctx.textBaseline = "top";
              ctx.fillStyle = "#111";
              ctx.fillText(label, node.x, node.y + 14);
            }}
          />
        </div>
      )}
    </div>
  );
}
